$.when($.ready).then(function(){fetch("https://landx.id/blog/rss/").then(e=>e.text()).then(e=>(new window.DOMParser).parseFromString(e,"text/xml")).then(e=>{const n=e.querySelectorAll("item");let s="",t="";n.forEach((e,n)=>{let l=null!=e.getElementsByTagName("title")[0]?e.getElementsByTagName("title")[0].childNodes[0].nodeValue:"",a=null!=e.getElementsByTagName("description")[0]?e.getElementsByTagName("description")[0].childNodes[0].nodeValue:"",i=null!=e.getElementsByTagName("link")[0]?e.getElementsByTagName("link")[0].childNodes[0].nodeValue:"",d=null!=e.getElementsByTagName("category")[0]?e.getElementsByTagName("category")[0].childNodes[0].nodeValue:"LandX";if(!(n<=2))return!0;s+=`\n                    <div class="carousel-item custom${0===n?" active":""}">\n                        <div class="col-12">\n                            <div class="card-body p-4" style="border-radius: 14px !important;">\n                                <p class="rss-categori">${d}</p>\n                                <p class="rss-title">${l}</p>\n                                <p class="rss-description">${a}</p>\n                                <div class="rss-btn-detail-blog">\n                                    <a href="${i}" target="_blank">Selengkapnya di blog</a>\n                                </div>\n                            </div>\n                        </div>\n                    </div>\n                    `,t+=`\n                    <div class="col-12 col-sm-12 col-md-4 col-lg-4">\n                        <div class="card-body p-4" style="border-radius: 14px !important;">\n                            <p class="rss-categori">${d}</p>\n                            <p class="rss-title">${l}</p>\n                            <p class="rss-description">${a}</p>\n                            <div class="rss-btn-detail-blog">\n                                <a href="${i}" target="_blank">Selengkapnya di blog</a>\n                            </div>\n                        </div>\n                    </div>\n                    `,document.getElementById("rss-mobile-view").innerHTML=s,document.getElementById("rss-desktop-view").innerHTML=t})})});